function setLanguage(e){fetch(`languages/${e}.json`).then(e=>e.json()).then(t=>{console.log("Translations loaded:",t),document.querySelectorAll("[data-translate]").forEach(e=>{var n=e.getAttribute("data-translate");t[n]&&(console.log(`Translating ${n} to: `+t[n]),"IMG"===e.tagName?e.alt=t[n]:e.textContent=t[n])})}).catch(e=>console.error("Error loading language file:",e))}window.onload=function(){document.querySelector(".nav__logo").style.animation="fadeInLogo 1s forwards",document.querySelector(".nav__menu").style.animation="slideInMenu 1s 0.5s forwards",document.querySelector(".nav__user").style.animation="fadeInUser 1s 1s forwards";let n=document.querySelectorAll(".nav__language-switcher button");n.forEach(e=>{e.addEventListener("click",()=>{n.forEach(e=>e.classList.remove("active")),e.classList.add("active")})})},document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".nav__item").forEach(e=>{let n=e.querySelector(".nav__submenu");e=e.querySelector(".nav__link");n&&e.addEventListener("click",function(e){e.preventDefault(),n.style.display="block"===n.style.display?"none":"block"})}),document.addEventListener("click",function(e){e.target.closest(".nav__menu")||document.querySelectorAll(".nav__submenu").forEach(e=>{e.style.display="none"})})});